let confKeys = [];

if (msg.confName === "/boot/adsbcot-config.txt") {
    confKeys = [
        "FEED_URL", 
        "POLL_INTERVAL", 
        "ALT_UPPER", 
        "ALT_LOWER"
    ]
} else if (msg.confName === "/boot/seatak-config.txt") {
    confKeys = [
        "NODE_ID",
        "COT_URL",
        "PYTAK_MULTICAST_LOCAL_ADDR",
        "PYTAK_TLS_CLIENT_CERT",
        "WIFI_AP_IP",
    ]
}

let k;

for (k of confKeys) {
    let confVal = getConfValue(k)
    if (confVal) {
        conf[k] = confVal
    }
}

flow.set(msg.confName, conf)

msg.payload = conf;

return msg;

